package difficultyLevels.easy.t859BuddyStrings

/**
 * Given two strings A and B of lowercase letters, return true if you can swap two letters in A so the result is equal to B, otherwise, return false.
 * Swapping letters is defined as taking two indices i and j (0-indexed) such that i != j and swapping the characters at A[i] and A[j].
 * For example, swapping at indices 0 and 2 in "abcd" results in "cbad".
 */
object Solution extends App {
  def buddyStrings(a: String, b: String): Boolean = {
    def check(indexes: Seq[Int]): Boolean = {
      a(indexes.head) == b(indexes(1)) &&
        b(indexes.head) == a(indexes(1))
    }

    def foo: Seq[Seq[Int]] = {
      for (i <- 0 until a.length;
           j <- i until a.length) yield Seq(i, j)
    }

    val value: Seq[Boolean] = for (i <- 0 until a.length) yield a(i) == b(i)
    val falseNumbs = value.count(false == _)
    falseNumbs match {
      case _ if a.length != b.length => false
      case _ if a.isEmpty && b.isEmpty => false
      case _ if a.length == 1 || b.length == 1 => false
      case 0 =>
        if (a.count(a.head == _) == a.length)
          true
        else
          foo.map(check).count(_ == true) > 0
      case 2 => check(value.zipWithIndex.filter(_._1 == false).map(_._2))
      case _ => false
    }
  }

  val a0 = "ab"
  val b0 = "ba"
  val a1 = "aaaaaaabc"
  val b1 = "aaaaaaacb"
  val a2 = ""
  val b2 = "ba"
  val a3 = "ab"
  val b3 = "ab"
  val a4 = "aa"
  val b4 = "aa"
  val a5 = ""
  val b5 = ""
  //println(a5.count(a5.head == _) +" "+ a5.length)

  val res0 = buddyStrings(a5, b5)
  println(res0)
}
